import { __decorate } from "tslib";
import { Component } from '@angular/core';
let GestionUsersComponent = class GestionUsersComponent {
    constructor(router, http) {
        this.router = router;
        this.http = http;
        this.getAllApprenants();
        this.getAllFormateurs();
    }
    async supprimer(id) {
        await this.http.delete("http://localhost:8080/api/deleteFormateur/" + id, { responseType: 'text' }).subscribe(data => {
            console.log(data);
            this.getAllFormateurs();
        });
    }
    ngOnInit() {
    }
    onClick(id) {
        this.http.delete("http://localhost:8080/api/deleteApprenant/" + id, { responseType: 'text' }).subscribe(result => {
            console.log(result);
            this.getAllApprenants();
        });
    }
    //Get all Apprenants ODK
    getAllApprenants() {
        this.http.get("http://localhost:8080/api/listApprenants").subscribe(list => {
            // console.log(list)
            this.apprenants = list;
        });
    }
    //Get All ODK's Formateurs
    getAllFormateurs() {
        this.http.get("http://localhost:8080/api/getAllFormateurs").subscribe(liste => {
            this.formateurs = liste;
        });
    }
    goToModifyApprenants() {
        this.router.navigate(['modifFormation']);
    }
};
GestionUsersComponent = __decorate([
    Component({
        selector: 'app-gestion-users',
        templateUrl: './gestion-users.component.html',
        styleUrls: ['./gestion-users.component.scss']
    })
], GestionUsersComponent);
export { GestionUsersComponent };
